j('
function gestisciModAssetto(event) {
	var target = event.target;
	if (target.tagName == "I") {
		if (event.ctrlKey)
			target.className = "fas fa-check-square fa-fw";
		else
			target.className = target.className == "far fa-square fa-fw" ? "far fa-check-square fa-fw" : (target.className == "far fa-check-square fa-fw" ? "fas fa-check-square fa-fw" : "far fa-square fa-fw");
	}
}
function salvaAssetto() {
	var checkboxes = document.getElementById("ModAssetto").querySelectorAll("i.fa-check-square"), assetto = [];
	for (var i = 0; i < checkboxes.length; ++i)
		assetto.push((checkboxes[i].className.indexOf("far") == 0 ? -1 : 1) * parseInt(checkboxes[i].dataset.id));
	document.querySelector("input[name=fDati]").value = JSON.stringify(assetto);
}
')

